SELECT cal.id,dp.cedula,dp.nombres_apellidos,dp.telefono, cal.n_expediente,fv.nombre_forma as tipo_violencia,fal.descripcion as forma_asesoria_legal,ta.nombre_tipo, cal.status, cal.comperencia, cal.fecha_cita,cal.fecha_registro, scl.nombre as estado, o.nombre_organismo FROM consolidado_asesoria_legal cal inner join datos_personales dp on dp.id = cal.id_datos_personal inner join formas_violencia fv on fv.id_forma = cal.id_forma_violencia inner join forma_asesoria_legal fal on fal.id= cal.id_tipo_asesoria inner join status_consol_legal scl on scl.id= cal.id_status_consol_legal inner join organismo o on o.id= cal.asesorado_defensor inner join tipo_asesoria ta on ta.id_tipo_asesoria = fal.id_tipo;